export default {
  methods:{
    sideNavClick(index,name) {
      this.$router.push({ name: name });//推荐使用名称切换
      let elements = this.$refs.navSidebar.children;
      for (let i = 0; i < elements.length; i++) {
        let navIconSvg = elements[i].getElementsByClassName("navIconSvg");
        let navContainer = elements[i].getElementsByClassName("navElementContainer__hexagonFill");
        if(i == index){
          navIconSvg[0].setAttribute("fill","#FFFFFF");
          navContainer[0].style = "translate: none; rotate: none; scale: none; transform-origin: 0px 0px; fill: rgb(255, 113, 148);";
        }else{
          navIconSvg[0].setAttribute("fill","#FF678D");
          navContainer[0].style = "";
        }
      }
    }
  }
}
